		list: '',
		toggleOn: '',
		toggleOff: '',
		balance: '',
		expand: '',
		shrink: '',
		doNotEnter: ''
	};

	interface Props {
		name: IconName;
		size?: string;
		color?: string;
		class?: string;
		weather?: boolean;
	}

	let { name, size = '24px', color = 'currentColor', class: className = '', weather = false }: Props = $props();

	// Weather icon color mapping
	const weatherColors: Record<string, string> = {
		sun: '#f59e0b',           // amber-500
		moon: '#6366f1',          // indigo-500
		sunCloud: '#f59e0b',      // amber-500
		moonCloud: '#6366f1',     // indigo-500
		cloud: '#6b7280',         // gray-500
		cloudDrizzle: '#3b82f6',  // blue-500
		cloudDrizzleHeavy: '#2563eb', // blue-600
		cloudRain: '#3b82f6',     // blue-500
		cloudShowers: '#0ea5e9',  // sky-500
		cloudSleet: '#06b6d4',    // cyan-500
		cloudSnow: '#0ea5e9',     // sky-500
		cloudBolt: '#eab308',     // yellow-500
		cloudBoltSun: '#eab308',  // yellow-500
		fog: '#9ca3af',           // gray-400
		smog: '#9ca3af',          // gray-400
		snowflake: '#0ea5e9',     // sky-500
		snowflakes: '#0ea5e9',    // sky-500
	};

	let iconColor = $derived(weather ? (weatherColors[name] || color) : color);

	let style = $derived(`width: ${size}; height: ${size}; color: ${iconColor}; display: inline-flex; align-items: center; justify-content: center; vertical-align: middle; flex-shrink: 0;`);
</script>

{#if icons[name]}
	<div {style} class={className}>
		{@html icons[name]}
	</div>
{/if}

<style>
	div :global(svg) {
		width: 100%;
		height: 100%;
		fill: currentColor;
		display: block;
	}
</style>
